mapper.select:
select * from(SELECT rownum as RNUM,s.* from (SELECT ITEM_NUM,ITEM_TYPE,ITEM_NAME,ITEM_PRICE,ITEM_DCP,ITEM_IMAGE1,ITEM_IMAGE2,ITEM_DATE,ITEM_STOCK FROM ITEM
Where ITEM_NAME like '%' || #{keyword} ||'%'
order by ITEM_NUM desc)s) WHERE RNUM >= #{START_NUM} AND RNUM <= #{END_NUM}

			SELECT  NT_NUM,NT_TITLE,NT_CONTENT,TO_CHAR(NT_DATE,'YYYY-MM-DD') NT_DATE,NT_TYPE
FROM NOTICE
WHERE NT_NUM=#{NT_NUM}

SELECT ET_NUM,ET_TITLE,ET_CONTENT,TO_CHAR(ET_SD,'YYYY-MM-DD')ET_SD,TO_CHAR(ET_FD,'YYYY-MM-DD')ET_FD 
FROM EVENT
WHERE ET_NUM=#{ET_NUM}

SELECT FAQ_NUM,FAQ_CONTENT,FAQ_TITLE,FAQ_CATE
FROM FAQ
WHERE FAQ_NUM=#{FAQ_NUM}

SELECT *FROM (SELECT rownum as RNUM,s.* FROM(SELECT MEM_NUM,MEM_ID,MEM_NAME,MEM_NICK,MEM_EMAIL,MEM_PHONE,MEM_ADDR1,MEM_ADDR2,MEM_RANK,MEM_JOINDATE,MEM_POINT,MEM_STACK FROM MEMBER WHERE MEM_DEL_GB='N'
AND MEM_ID like '%' || #{keyword} ||'%'
)S)
WHERE RNUM>=#{START_NUM} AND RNUM<=#{END_NUM}

SELECT MEM_NUM,MEM_ID,MEM_NAME,MEM_NICK,MEM_EMAIL,MEM_PHONE,MEM_ADDR1,MEM_ADDR2,MEM_RANK,MEM_JOINDATE,MEM_POINT,MEM_STACK
FROM MEMBER 
WHERE MEM_NUM=#{MEM_NUM}

mapper.update:
	UPDATE MEMBER
SET MEM_DEL_GB='Y' 
WHERE  MEM_NUM=#{MEM_NUM}
