choose.when:
SELECT * FROM (SELECT rownum as RNUM,s.* from (select ITEM_NUM,ITEM_TYPE,ITEM_NAME,ITEM_PRICE,ITEM_DCP,ITEM_IMAGE1,ITEM_IMAGE2,ITEM_DATE,ITEM_STOCK,ITEM_PRICE-(ITEM_PRICE*(ITEM_DCP/100)) AS ITEM_FP from item
WHERE ITEM_TYPE=#{type}
WHERE ITEM_NAME like '%' || #{keyword} ||'%'
order by ITEM_NUM desc)s)
WHERE RNUM >= #{START_NUM} AND RNUM <= #{END_NUM}
WHERE RNUM >= 1 AND RNUM <= 100

SELECT * FROM (SELECT ROWNUM as RNUM,s.* FROM (SELECT ITEM_NUM,ITEM_TYPE,ITEM_NAME,ITEM_PRICE,ITEM_DCP,ITEM_IMAGE1,ITEM_IMAGE2,ITEM_DATE,ITEM_STOCK,ITEM_PRICE-(ITEM_PRICE*(ITEM_DCP/100)) AS ITEM_FP,NVL(CN,0)AS CN FROM (SELECT o.ITEM_NUM,COUNT(o.ITEM_NUM)AS CN FROM ORDERS o GROUP BY o.ITEM_NUM) vw_order RIGHT JOIN ITEM USING (ITEM_NUM)
WHERE ITEM_TYPE=#{type}
WHERE ITEM_NAME like '%' || #{keyword} ||'%'
ORDER BY CN DESC)s)
WHERE RNUM >= #{START_NUM} AND RNUM <= #{END_NUM}
WHERE RNUM >= 1 AND RNUM <= 100

SELECT * FROM (SELECT rownum as RNUM,s.* from (select ITEM_NUM,ITEM_TYPE,ITEM_NAME,ITEM_PRICE,ITEM_DCP,ITEM_IMAGE1,ITEM_IMAGE2,ITEM_DATE,ITEM_STOCK,ITEM_PRICE-(ITEM_PRICE*(ITEM_DCP/100)) AS ITEM_FP from item
WHERE ITEM_TYPE=#{type}
WHERE ITEM_NAME like '%' || #{keyword} ||'%'
order by ITEM_FP asc)s)
WHERE RNUM >= #{START_NUM} AND RNUM <= #{END_NUM}
WHERE RNUM >= 1 AND RNUM <= 100

SELECT * FROM (SELECT rownum as RNUM,s.* from (select ITEM_NUM,ITEM_TYPE,ITEM_NAME,ITEM_PRICE,ITEM_DCP,ITEM_IMAGE1,ITEM_IMAGE2,ITEM_DATE,ITEM_STOCK,ITEM_PRICE-(ITEM_PRICE*(ITEM_DCP/100)) AS ITEM_FP from item
WHERE ITEM_TYPE=#{type}
WHERE ITEM_NAME like '%' || #{keyword} ||'%'
order by ITEM_FP desc)s)
WHERE RNUM >= #{START_NUM} AND RNUM <= #{END_NUM}
WHERE RNUM >= 1 AND RNUM <= 100

choose.otherwise:
